import React, { useState, useEffect } from "react";
import { Link, Navigate } from "react-router-dom";
import axios from "axios";
import google from '../components/img/google.png';
import apple from '../components/img/apple.png';
import formImg from '../components/img/form_img.jpg';
import Navbar from "../components/navbar";
import Footer from '../components/footer';
import '../components/css/login.css';


function Login() {
    const [formData, setFormData] = useState({
        email: '',
        password: ''
    });
    const [error, setError] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [redirect, setRedirect] = useState(false);
    const [isMobileView, setIsMobileView] = useState(window.innerWidth <= 800);

    // Funzione che verifica quando la dimensione dello schermo è minore di 800px
    useEffect(() => {
        const handleResize = () => {
            setIsMobileView(window.innerWidth <= 800);
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        }
    }, []);

    // Funzione che invia una richiesta al server Django e restituisce la risposta
    async function handleSubmit(e) {
        e.preventDefault();
        try {
            const response = await axios.post('/api/register/', formData); // Risposta del server Django

            const csrfToken = response.data.csrfToken; // Ottieni il token CSRF
            axios.defaults.headers.common['X-CSRFToken'] = csrfToken; // Imposta il token CSRF come header della richiesta

            localStorage.setItem('token', response.data.token) // Salva il token JWT nel local storage
            setIsLoggedIn(true);
            setRedirect(true);
        } catch (error) {
            setError(error.response.data.error); // Errore nella registrazione restituito dal server Django
        }
    }

    // Funzione che permette di modificare gli input
    function handleChange(e) {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    }

    // // FUnzione che invia una richiesta al server Django e restituisce la risposta
    async function handleSubmit(e) {
        e.preventDefault();
        try {
            const response = await axios.post('/api/login/', formData); // Risposta del server Django

            const csrfToken = response.data.csrfToken; // Ottieni il token CSRF
            axios.defaults.headers.common['X-CSRFToken'] = csrfToken; // Imposta il token CSRF come header della richiesta

            localStorage.setItem('token', response.data.token) // Salva il token JWT nel local storage
            setIsLoggedIn(true);
            setRedirect(true);
        } catch (error) {
            setError(error.response.data.error); // Errore nel login restituito dal server Django
        }
    }

